import React, { useState, useEffect } from "react";
import "../styles/home.css";

const ProgressBar = () => {
  const [totalAmount, setTotalAmount] = useState(0);
  const goal = 10000;

  useEffect(() => {
    // Exemple temporaire de montant collecté
    setTotalAmount(5000);
  }, []);

  return (
    <div className="progress-container">
      <div className="progress-bar" style={{ height: `${(totalAmount / goal) * 100}%` }} />
      <p>{totalAmount}€ / {goal}€</p>
    </div>
  );
};

export default ProgressBar;
